import React from "react";
import Items from "./Items";
import Charts from "./Charts";
import Shipment from "./Shipment";
import axios from "axios";
class Menu extends React.Component {
  state = {
    shipments: [],
    show: false,
    items: [
      [{ nameSHIP: "XD", name: "React", show: false }],
      [{ nameSHIP: "XD", name: "filon", show: false }]
    ] //shipment name shouldbethere
  };
  showChange = e => {
    // console.log(e);

    this.state.items.map(item => {
      item.map(item => {
        if (e.name === item.name) {
          console.log(item.show);

          item.show
            ? this.setState({
                items: [...this.state.items, (item.show = false)]
              })
            : this.setState({
                items: [...this.state.items, (item.show = true)]
              });
        }
      });

      // if (shipment.name === item.name) {
      //   item.show
      //     ? this.setState({ items: (item.show = false) })
      //     : this.setState({ items: (item.show = true) });
      // } else {
      //   console.log("he");
      // }
    });
  };
  returnItems = () => {
    return <Items />;
  };
  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });

      list.map(shipment =>
        this.setState({
          items: [...this.state.items, shipment.items]
        })
      );
      console.log(this.state);
    } catch (e) {
      this.props.props.history.push("/login");
      localStorage.clear();
    }
  }
  render() {
    return (
      <>
        <div className="content-wrapper" style={{ minHeight: "901px" }}>
          <aside className="main-sidebar">
            {/* sidebar: style can be found in sidebar.less */}
            <section className="sidebar">
              >{/* sidebar menu: : style can be found in sidebar.less */}
              <ul className="sidebar-menu" data-widget="tree">
                <li className="header">LIST OF SHIPMENTS</li>

                {this.state.shipments.map(shipment => (
                  <Shipment
                    shipment={shipment}
                    id={shipment.id}
                    name={shipment.name}
                    removeShipmentFromState={this.removeShipmentFromState}
                    key={shipment.id}
                    onClick={() => this.showChange(shipment)}
                  />
                ))}

                {/* <Charts /> */}
              </ul>
            </section>

            {/* /.sidebar */}
          </aside>

          {this.state.items.map(item => (
            <div style={{ display: item.show ? "block" : "none" }}>
              <Items
                name={item.name}
                nameSHIP={item.nameSHIP}
                show={item.show}
              />
            </div>
          ))}
        </div>
      </>
    );
  }
}

export default Menu;
