import React from "react";
import Items from "./Items";
import Charts from "./Charts";
import Shipment from "./Shipment";
import axios from "axios";
class Menu extends React.Component {
  state = {
    shipments: [],
    show: false,
    items: [{ nameSHIP: "XD" }] //shipment name shouldbethere
  };
  showChange = e => {
    // this.state.show
    //   ? this.setState({ show: false })
    //   : this.setState({ show: true });
    console.log(e);
    this.state.shipments.map(shipment => {
      if (shipment.id === e) {
        console.log("xd");
      } else {
        console.log("he");
      }
    });
  };
  returnItems = () => {
    return <Items />;
  };
  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });
    } catch (e) {
      this.props.props.history.push("/login");
      localStorage.clear();
    }
  }
  render() {
    return (
      <>
        <div className="content-wrapper" style={{ minHeight: "901px" }}>
          <aside className="main-sidebar">
            {/* sidebar: style can be found in sidebar.less */}
            <section className="sidebar">
              >{/* sidebar menu: : style can be found in sidebar.less */}
              <ul className="sidebar-menu" data-widget="tree">
                <li className="header">LIST OF SHIPMENTS</li>

                {this.state.shipments.map(shipment => (
                  <Shipment
                    shipment={shipment}
                    id={shipment.id}
                    name={shipment.name}
                    removeShipmentFromState={this.removeShipmentFromState}
                    key={shipment.id}
                    onClick={() => this.showChange(shipment.id)}
                  />
                ))}

                <Charts />
              </ul>
            </section>

            {/* /.sidebar */}
          </aside>

          {this.state.items.map(item => (
            <div style={{ display: this.state.show ? "block" : "none" }}>
              <Items name={item.name} nameSHIP={item.nameSHIP} />
            </div>
          ))}
        </div>
      </>
    );
  }
}

export default Menu;
