import React from "react";
import Items from "./Items";
import Charts from "./Charts";
import Shipment from "./Shipment";
import axios from "axios";
import Item from "./Item";
import NewShipment from "./NewShipment";
class Menu extends React.Component {
  state = {
    shipments: [], //shipment name shouldbethere
    openedShipmentName: "",
    isShipmentOpen: false
  };
  showChange = e => {
    console.log(e);
    if (this.state.openedShipmentName === e.name) {
      this.setState({ isShipmentOpen: false });
      this.setState({ openedShipmentName: "" });
    } else {
      this.setState({ openedShipmentName: e.name });
      this.setState({ isShipmentOpen: true });
    }
    console.log(this.state);
  };

  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });

      console.log(this.state);
    } catch (e) {
      alert("Faild to load Shipments");
    }
  }
  render() {
    return (
      <>
        {/* <div className="content-wrapper" style={{ minHeight: "901px" }}> */}
        <aside className="main-sidebar">
          {/* sidebar: style can be found in sidebar.less */}
          <section className="sidebar">
            >{/* sidebar menu: : style can be found in sidebar.less */}
            <ul className="sidebar-menu" data-widget="tree">
              <li className="header">LIST OF SHIPMENTS</li>
              <NewShipment onClick={e => this.showChange(this.state)} />
              {this.state.shipments.map(shipment => (
                <Shipment
                  shipment={shipment}
                  id={shipment.id}
                  name={shipment.name}
                  removeShipmentFromState={this.removeShipmentFromState}
                  key={shipment.id}
                  onClick={() => this.showChange(shipment)}
                />
              ))}

              {/* <Charts /> */}
            </ul>
          </section>

          {/* /.sidebar */}
        </aside>
        <div className="content-wrapper" style={{ minHeight: "901px" }}>
          {/* {this.state.shipments.map(shipment => {
            if (
              shipment.name === this.state.openedShipmentName &&
              this.state.isShipmentOpen
            ) {
              return (
                <Items
                  //name={item.name}
                  nameSHIP={shipment.name}
                  listOfItems={shipment.items.map(item => {
                    return (
                      <Item itemName={item.code} deleteItem={item.deleteItem} />
                    );
                  })}
                />
              );
            }
          })} */}
          <div className="row">
            <div className="col-sm-12">
              <table
                id="example2"
                className="table table-bordered table-hover dataTable"
                role="grid"
                aria-describedby="example2_info"
              >
                <thead>
                  <tr role="row">
                    <th
                      className="sorting_asc"
                      tabIndex={0}
                      aria-controls="example2"
                      rowSpan={1}
                      colSpan={1}
                      aria-sort="ascending"
                      // aria-label="Rendering engine: activate to sort column descending"
                    >
                      <div className="input-group margin">
                        <input
                          type="text"
                          className="form-control"
                          placeholder="Name"
                        />
                        <div className="input-group-btn">
                          <button
                            type="button"
                            className="btn btn-block btn-info"
                          >
                            Add Item
                          </button>
                        </div>
                      </div>
                    </th>
                  </tr>
                </thead>
              </table>
            </div>
          </div>
        </div>
        {/* </div> */}
      </>
    );
  }
}

export default Menu;
