import React from "react";
import DeleteItemButton from "./DeleteItemButton/DeleteItemButton";
import "./Item.css";

const Item = props => {
  return (
    <div>
      <div className="item">
        Item id: {props.id}
        <br />
        Item code: {props.code}
        <DeleteItemButton removeItemFromState={props.removeItemFromState} />
      </div>
    </div>
  );
};

export default Item;
