import React from "react";
import { Redirect } from "react-router";
import Shipment from "../Shipment/Shipment";
import axios from "axios";
import { IoIosAddCircleOutline } from "react-icons/io";
class Main extends React.Component {
  state = {
    shipments: []
  };
  /*addShipmentToState = id => {
    const currentArray = [...this.state.shipments];
    const newArray = currentArray.push("xd");
    this.setState({
      shipments: newArray
    });
  }*/ removeShipmentFromState = id => {
    const currentArray = [...this.state.shipments];
    const newArray = currentArray.filter(item => item.id !== id);
    this.setState({
      shipments: newArray
    });
  };

  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });
    } catch (e) {
      console.log(e);
    }
  }

  render() {
    const { props, state } = this;
    console.log(state);
    if (!props.isLoggedIn) {
      return <Redirect to="/login" />;
    }
    return (
      <div>
        {state.shipments.map(item => (
          <Shipment
            shipment={item}
            id={item.id}
            name={item.name}
            removeShipmentFromState={this.removeShipmentFromState}
          />
        ))}
        <div className="add-shipment-div">
          <button className="add-shipment" onClick={this.addShipmentToState}>
            <IoIosAddCircleOutline className="add-icon" />
            <p>Add shipment</p>
          </button>
          <div className="shipment-inputs">
            <input />
            <input />
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
