import React from "react";
import Items from "./Items";
import Charts from "./Charts";
import Shipment from "./Shipment";
import axios from "axios";
class Menu extends React.Component {
  state = {
    shipments: [],
    show: false
  };
  showChange = () => {
    this.state.show
      ? this.setState({ show: false })
      : this.setState({ show: true });
  };

  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });
    } catch (e) {
      this.props.props.history.push("/login");
      localStorage.clear();
    }
  }
  render() {
    return (
      <>
        <aside className="main-sidebar">
          {/* sidebar: style can be found in sidebar.less */}
          <section className="sidebar" onClick={this.showChange}>
            >{/* sidebar menu: : style can be found in sidebar.less */}
            <ul className="sidebar-menu" data-widget="tree">
              <li className="header">LIST OF SHIPMENTS</li>

              {this.state.shipments.map(shipment => (
                <Shipment
                  shipment={shipment}
                  id={shipment.id}
                  name={shipment.name}
                  removeShipmentFromState={this.removeShipmentFromState}
                  key={shipment.id}
                  listOfItems={"1"}
                />
              ))}

              <Charts />

              {/* <li className="treeview">
                <a href="#">
                  <i className="fa fa-pie-chart" />
                  <span>Charts</span>
                  <span className="pull-right-container">
                    <i className="fa fa-angle-left pull-right" />
                  </span>
                </a>
                <ul className="treeview-menu">
                  <li>
                    <a href="#">
                      <i className="fa fa-circle-o" /> ChartJS
                    </a>
                  </li>
                </ul>
              </li> */}
            </ul>
          </section>

          {/* /.sidebar */}
        </aside>

        {this.state.shipments.map(shipment => (
          <div style={{ display: this.state.show ? "block" : "none" }}>
            <Items name={shipment.name} />
          </div>
        ))}
      </>
    );
  }
}

export default Menu;
