import React from "react";
import { Redirect } from "react-router";
import Shipment from "../Shipment/Shipment";
import axios from "axios";
import { IoIosAddCircleOutline } from "react-icons/io";
import "./Main.css";

class Main extends React.Component {
  state = {
    shipments: [],
    newId: "",
    newName: ""
  };
  newShipmentId = event => {
    this.setState({
      newId: event.target.value
    });
  };
  newShipmentName = event => {
    this.setState({
      newName: event.target.value
    });
  };
  addShipment = async event => {
    this.addShipmentToState();
    this.clearInputs();

    try {
      await axios.post(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          id: this.state.newId,
          name: this.state.newName
        },
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
    } catch (e) {
      console.log(e);
    }
  };
  addShipmentToState = () => {
    const ship = {
      id: this.state.newId,
      name: this.state.newName,
      items: []
    };
    //{ id: "46", shipment_id: "45", name: "Maciek", code: "Maciek" }
    const currentArray = [...this.state.shipments];
    const newArray = currentArray;
    newArray.push(ship);
    this.setState({
      shipments: newArray
    });
  };
  checkAdding = () => {
    const check = this.state.shipments;
    const checkNewId = this.state.newId;
    let isThereId = false;

    check.map(el => {
      if (el.id === checkNewId) {
        isThereId = true;
      }

      return isThereId;
    });
    if (!isThereId) {
      //ZROB SPRAWDZANIE CZY JUZTAKI JEST !!!
      this.addShipment();
    }
  };
  removeShipmentFromState = id => {
    console.log(id);
    const currentArray = [...this.state.shipments];
    const newArray = currentArray.filter(item => item.id !== id);
    this.setState({
      shipments: newArray
    });
  };

  async componentDidMount() {
    try {
      const data = await axios.get(
        `https://api.shipments.test-y-sbm.com/shipment`,
        {
          headers: {
            Authorization: `bearer ${localStorage.token}`
          }
        }
      );
      const list = data.data.data.shipments;
      console.log(list);
      this.setState({
        shipments: list
      });
    } catch (e) {
      console.log(e);
    }
  }
  clearInputs = () => {
    this.setState({
      newId: "",
      newName: ""
    });
  };
  logOut = () => {
    this.props.history.push("/login");
    localStorage.clear();
  };

  render() {
    const { props, state } = this;
    console.log(state);
    if (!props.isLoggedIn) {
      return <Redirect to="/login" />;
    }

    return (
      <div>
        {state.shipments.map(item => (
          <Shipment
            shipment={item}
            id={item.id}
            name={item.name}
            removeShipmentFromState={this.removeShipmentFromState}
          />
        ))}
        <div className="add-shipment-div">
          <button className="add-shipment" onClick={this.checkAdding}>
            <IoIosAddCircleOutline className="add-icon" />
            <p>Add shipment</p>
          </button>
          <div className="shipment-inputs">
            <input
              placeholder="id"
              onChange={this.newShipmentId}
              value={this.state.newId}
            />
            <input
              placeholder="name"
              onChange={this.newShipmentName}
              value={this.state.newName}
            />
          </div>
        </div>

        <div className="logout-div" onClick={this.logOut}>
          <button>
            <p>Log out</p>
          </button>
        </div>
      </div>
    );
  }
}
//this.addShipmentToState
export default Main;
